import React from 'react';
import NavBar from '../../nav/nav.js'
import ShopNav from '../shopnav/shopnav.js'
import Product from './product/product.js'
import productlist from './productlist.json';
import Footer from '../../footer/footer.js'
import './products.css'

const Products = (props) => {

    const LoadProducts = (props) => {

        let products = [];

        for(let i = Number(props.first); i <= Number(props.last); i++) {
            products.push(
                <div>
                    <Product {...productlist[i]} />
                </div>
            )
        }

        return products;

        // return productlist.map((product) => {
        //     return (
        //         <div>
        //             <Product {...product} />
        //         </div>
        //     )
        // })
    }

    return (
        <div>
            <NavBar />
            <div className="products">
                <LoadProducts first="0" last="5"/>
                <div style={{backgroundImage: "/images/background/promo-bg.png", width: "100%"}}>

                    <h3 id="promo">Get 10% Off</h3>
                </div>
                <LoadProducts first="6" last="8"/>
            </div>
            <Footer />
        </div>
    );
};

export default Products;
