import logo from './logo.svg';
import './App.css';
import Landing from './landing/landing.js'
import About from './about/about.js';
import Shop from './shop/shop.js';
import Products from './shop/products/products.js'
import { Switch, Route } from 'react-router-dom';
import axios from 'axios';

function App() {
  return (
    <div>

    <Switch>
      <Route exact path='/' render={(props) => <Landing {...props}/>} />
      <Route exact path='/shop' render={(props) => <Shop {...props}/>} />
      <Route exact path='/about' render={(props) => <About {...props}/>} />
      <Route exact path='/products/:productType' render={(props) => <Products {...props} /> } />
    </Switch>

    </div>
  );
}

export default App;

// axios.post('https://ironrest.herokuapp.com/voodoochildren', {hello: 'children', id:Math.random()})
// axios.get('https://ironrest.herokuapp.com/voodoochildren').then(res => console.log(res));

function makeid(length) {
  var result           = '';
  var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  var charactersLength = characters.length;
  for ( var i = 0; i < length; i++ ) {
    result += characters.charAt(Math.floor(Math.random() * charactersLength));
  }
  return result;
}



// first we check if the user has already used the page
// check local storage to see if they have
// if they haven't we have to create a new user in the api and then update whenever the shopping cart changes
// if they have then we get the user information from the database like objects in shopping cart


let userID;
let userData;

if(!localStorage.getItem('userID')) {
  userID = makeid(16)
  localStorage.setItem('userID', `${userID}`);
  axios.post(`https://ironrest.herokuapp.com/voodoochildren`, {user: userID, cart: ['shirt1', 'shirt2']});
} else {
  axios.get(`https://ironrest.herokuapp.com/voodoochildren/${localStorage.getItem('userID')}`).then(res => userData);
}



axios.get(`https://ironrest.herokuapp.com/findAll/voodoochildren?userID=${localStorage.getItem('userID')}`).then(res => userData = res);

if(userData) {
  console.log('user found')
} else {
  console.log('user not found')
}
